#include <gtest/gtest.h>
#include "../Utilities/Utilities.h"


// Binary Exponentiation: https://www.dcode.fr/exponentiation-calculation

TEST(TestBinaryExponentiation, BaseCases) {
    // Basic cases with small bases and exponents
    EXPECT_EQ(Utilities::binaryExponentiation(2, 2), 4);   // 2^2 = 4
    EXPECT_EQ(Utilities::binaryExponentiation(3, 3), 27);  // 3^3 = 27
    EXPECT_EQ(Utilities::binaryExponentiation(5, 0), 1);   // 5^0 = 1
    EXPECT_EQ(Utilities::binaryExponentiation(0, 5), 0);   // 0^5 = 0
}

TEST(TestBinaryExponentiation, EdgeCases) {
    // Edge cases with special inputs
    EXPECT_EQ(Utilities::binaryExponentiation(1, 100), 1); // 1^100 = 1
    EXPECT_EQ(Utilities::binaryExponentiation(0, 0), 1);   // 0^0 is conventionally 1
    EXPECT_EQ(Utilities::binaryExponentiation(-2, 2), 4);  // (-2)^2 = 4
    EXPECT_EQ(Utilities::binaryExponentiation(-2, 3), -8); // (-2)^3 = -8
}

TEST(TestBinaryExponentiation, TwoDigitExponents) {
    // Cases with large exponents to test efficiency
    EXPECT_EQ(Utilities::binaryExponentiation(2, 10), 1024);  // 2^10 = 1024
    EXPECT_EQ(Utilities::binaryExponentiation(3, 20), 3486784401); // 3^20
}

TEST(TestBinaryExponmentiation, FiveDigitExponents) {

    // 2^50000
    boost::multiprecision::cpp_int r {""};
    EXPECT_EQ(Utilities::binaryExponentiation(2, 50000),r);

    // 5^99999
    boost::multiprecision::cpp_int r2 {""};
    EXPECT_EQ(Utilities::binaryExponentiation(5, 99999), r2);
}

// Main entry point for the test runner
int main(int argc, char **argv) {
    ::testing::InitGoogleTest(&argc, argv);
    return RUN_ALL_TESTS();
}